package com.adm.example;

public class For_Loop_Ex {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("ADM");
		int a=5;
		for(int i=5;i>=1;i--) {
			for(int j=1;j<=i;j++) {
				System.out.print(i);
			}
			System.out.println();
		}
		for(int x=2;x<=a;x++) {
			for(int z=1;z<=x;z++) {
				System.out.print(x);
			}
			System.out.println();
		}

	}

}
